INSERT INTO course VALUES (39,03212728,'媒体计算',92036000,'计算机与控制工程学院','Media Computing','  本课程假设选课学生了解《计算机视觉基础》、《计算机图形学》、《数字图像处理》等本科生课程中所讲授的基本知识。与本科生课程侧重基本技术原理和开发过程的学习不同，该研究生课程侧重学生科研能力的培养。',32,'   ','  计算机图形学基础（第二版），孙家广;胡事民，北京：清华大学出版社，2009','  课程设计：60%');
INSERT INTO course VALUES (40,03212730,'可信计算讨论班（1）',92036000,'计算机与控制工程学院','Trusted Computing（1）','"可信计算是当前计算机科学技术和信息安全领域非常重要的研究方向，我国在此领域与国际同步起步，研究成果处于领先地位。本课程面向控制科学与工程类和计算机科学技术类的硕士和博士研究生。课程目标是向研究生系统地介绍可信计算技术和理论知识，并跟踪该领域新的研究动态和发展趋势，探讨该领域中新的研究内容，使学生能够结合导师的研究方向，确定相关的研究课题和研究内容。课程的主要内容包括：可信计算的产生背景与发展史、可信计算的研究现状与发展趋势简介；可信计算的理论与技术介绍；可信计算相关的发展趋势与应用探索等。"',16,'1．徐拾义，《可信计算系统设计与分析》，北京：清华大学出版社，2006年7月2．Sean W. Smith，《可信计算平台：设计与应用》，清华大学出版社，北京，2006年10月3．相关的最新的研究文献等','Chris (EDT) Mitchell，Trusted Computing，IET，2006','文献综述报告');
INSERT INTO course VALUES (41,03212731,'可信计算讨论班（2）',92036000,'计算机与控制工程学院','Trusted Computing（2）','"可信计算是当前计算机科学技术和信息安全领域非常重要的研究方向，我国在此领域与国际同步起步，研究成果处于领先地位。本课程面向控制科学与工程类和计算机科学技术类的硕士和博士研究生。课程目标是向研究生系统地介绍可信计算技术和理论知识，并跟踪该领域新的研究动态和发展趋势，探讨该领域中新的研究内容，使学生能够结合导师的研究方向，确定相关的研究课题和研究内容。课程的主要内容包括：可信计算的产生背景与发展史、可信计算的研究现状与发展趋势简介；可信计算的理论与技术介绍；可信计算相关的发展趋势与应用探索等。"',16,'1．徐拾义，《可信计算系统设计与分析》，北京：清华大学出版社，2006年7月2．Sean W. Smith，《可信计算平台：设计与应用》，清华大学出版社，北京，2006年10月3．相关的最新的研究文献等','Chris (EDT) Mitchell，Trusted Computing，IET，2006','文献综述报告');
