INSERT INTO course VALUES (109,03222725,'软件定义网络',92036000,'计算机与控制工程学院','Software Defined Networking','  本课程讲述SDN的设计思想和体系架构，分析SDN中控制转发分离和可编程性等重要概念，介绍以OpenFlow为代表的SDN南向接口协议和北向接口协议。同时，要求学生观看普林斯顿大学的SDN公开课视频并讨论，在Mininet等环境下进行仿真实验。',32,NULL,'  2. Thomas Nadeau D，Ken Gray，软件定义网络: SDN与OpenFlow解析，人民邮电出版社，2014，ISBN:9787115351470',' 文献综述     3. http://yuba.stanford.edu/foswiki/bin/view/OpenFlow/Mininet ');
