INSERT INTO course VALUES (111,03222728,'媒体计算',92036000,'计算机与控制工程学院','Media Computing','  本课程假设选课学生了解《计算机视觉基础》、《计算机图形学》、《数字图像处理》等本科生课程中所讲授的基本知识。与本科生课程侧重基本技术原理和开发过程的学习不同，该研究生课程侧重学生科研能力的培养。',32,'   ','  计算机图形学基础（第二版），孙家广;胡事民，北京：清华大学出版社，2009','  课程设计：60% ');
