INSERT INTO course VALUES (117,03231003,'专业实践2',92036000,'计算机与控制工程学院','Engineering Practice 2','由研究生导师安排研究生进行与专业相关的工程项目实践，可以在校内完成，也可以到合作企业完成。通过项目实践以及与导师的定期讨论，提高专业硕士的工程实践能力。',32,NULL,NULL,'专业实践报告');
